SELECT Tbl_Family_Names.Family_Number, Tbl_Family_Names.Family_Name, Tbl_Requirements.Requirement_Number, Tbl_Requirements.Requirement_Description, Tbl_Requirements.Requirement_Score, Tbl_Requirements.Requirement_Special_Considerations, Tbl_Requirements.Requirement_Special_Considerations_Score, Tbl_Objectives.Objective_Number, Tbl_Objectives.Objective_Text, Tbl_Objectives.Objective_Satisfied, Tbl_Objectives.Objective_Other_Than_Satisfied, Tbl_Objectives.Typical_Technologies_Used, Tbl_Objectives.Typical_Questions_Asked, Tbl_Objectives.Observation
FROM Tbl_Objectives INNER JOIN ((Tbl_Requirements INNER JOIN LnkTbl_RequirementsToObjectives ON (Tbl_Requirements.Requirement_Number = LnkTbl_RequirementsToObjectives.Requirement_Number) AND (Tbl_Requirements.[Requirement_Number] = LnkTbl_RequirementsToObjectives.[Requirement_Number])) INNER JOIN (Tbl_Family_Names INNER JOIN LnkTbl_RequirementsToFamilies ON Tbl_Family_Names.Family_Number = LnkTbl_RequirementsToFamilies.Family_Number) ON Tbl_Requirements.[Requirement_Number] = LnkTbl_RequirementsToFamilies.[Requirement_Number]) ON Tbl_Objectives.Objective_Number = LnkTbl_RequirementsToObjectives.Objective_Number;
