SELECT Tbl_Family_Names.Sorting, Tbl_Family_Names.Family_Number, Tbl_Requirements.Req_Sorting, Tbl_Requirements.Requirement_Number, PlainText([Tbl_Requirements].[Requirement_Description]) AS Requirement_Description, Tbl_Requirements.Requirement_Score AS Points, Tbl_Requirements.Requirement_Other_Than_Satisfied, Tbl_Requirements.Requirement_Special_Considerations_Score AS SC_Points, IIf([Requirement_Special_Considerations_Satisfied]=True,[Requirement_Special_Considerations_Score],[Requirement_Score]) AS TotalDeducted, Tbl_Objectives.Objective_Number, Tbl_Objectives.Objective_Text, Tbl_Objectives.Objective_Other_Than_Satisfied, Tbl_Requirements.POAM, Tbl_Requirements.POAM_Date, Tbl_Requirements.CMMC_Practice_Number, Tbl_Objectives.Validation_Text
FROM Tbl_Objectives INNER JOIN ((Tbl_Requirements INNER JOIN (Tbl_Family_Names INNER JOIN LnkTbl_RequirementsToFamilies ON Tbl_Family_Names.Family_Number = LnkTbl_RequirementsToFamilies.Family_Number) ON Tbl_Requirements.Requirement_Number = LnkTbl_RequirementsToFamilies.Requirement_Number) INNER JOIN LnkTbl_RequirementsToObjectives ON Tbl_Requirements.Requirement_Number = LnkTbl_RequirementsToObjectives.Requirement_Number) ON Tbl_Objectives.Objective_Number = LnkTbl_RequirementsToObjectives.Objective_Number
WHERE (((Tbl_Objectives.Objective_Other_Than_Satisfied)=True))
ORDER BY Tbl_Family_Names.Sorting, Tbl_Requirements.Req_Sorting, Tbl_Objectives.Objective_Number;
