SELECT DISTINCT Tbl_Objectives.Objective_Number, Tbl_Requirements.Requirement_Number, Tbl_Objectives.Documents_Examined_Details, Tbl_Objectives.Validation_Text, Tbl_Objectives.Notes, Tbl_Objectives.Objective_Text, Tbl_Objectives.SME_Interviewed_Names, Tbl_Requirements.Requirement_Description, Tbl_Requirements.Requirement_Discussion, Tbl_Objectives.Standard, Tbl_Requirements.Req_Sorting
FROM Tbl_Requirements INNER JOIN (Tbl_Objectives INNER JOIN LnkTbl_RequirementsToObjectives ON Tbl_Objectives.Objective_Number = LnkTbl_RequirementsToObjectives.Objective_Number) ON Tbl_Requirements.Requirement_Number = LnkTbl_RequirementsToObjectives.Requirement_Number
WHERE (((Tbl_Objectives.Documents_Examined_Details) Like "*" & forms!FRM_SearchMulti!SrchDocs & "*")) Or (((Tbl_Objectives.Validation_Text) Like "*" & forms!FRM_SearchMulti!SrchVald & "*")) Or (((Tbl_Objectives.Notes) Like "*" & forms!FRM_SearchMulti!SrchNote & "*")) Or (((Tbl_Objectives.Objective_Text) Like "*" & forms!FRM_SearchMulti!SrchObjs & "*")) Or (((Tbl_Objectives.SME_Interviewed_Names) Like "*" & forms!FRM_SearchMulti!SrchIntv & "*")) Or (((Tbl_Requirements.Requirement_Description) Like "*" & forms!FRM_SearchMulti!SrchReqs & "*")) Or (((Tbl_Requirements.Requirement_Discussion) Like "*" & forms!FRM_SearchMulti!SrchDisc & "*")) Or (((Tbl_Objectives.Standard) Like "*" & forms!FRM_SearchMulti!SrchStan & "*"))
ORDER BY Tbl_Requirements.Req_Sorting;
