SELECT Tbl_Family_Names.Sorting, Tbl_Family_Names.Family_Number, Tbl_Family_Names.Family_Name, Tbl_Requirements.Requirement_Satisfied, Tbl_Requirements.Requirement_Other_Than_Satisfied, Tbl_Requirements.Requirement_Special_Considerations_Satisfied, Tbl_Requirements.Requirement_Score, Tbl_Requirements.Requirement_Special_Considerations_Score, IIf([Requirement_Satisfied]=True,0,IIf([Requirement_Other_Than_Satisfied]=True,[Requirement_Score],IIf([Requirement_Special_Considerations_Satisfied]=True,[Requirement_Special_Considerations_Score],0))) AS TotalDeducted, Tbl_Family_Names.CMMC_Domain
FROM Tbl_Requirements INNER JOIN (Tbl_Family_Names INNER JOIN LnkTbl_RequirementsToFamilies ON Tbl_Family_Names.Family_Number = LnkTbl_RequirementsToFamilies.Family_Number) ON Tbl_Requirements.Requirement_Number = LnkTbl_RequirementsToFamilies.Requirement_Number
ORDER BY Tbl_Family_Names.Sorting;
